import React from 'react';
import { View, Text, StyleSheet, TouchableOpacity } from 'react-native';

export default function ProductCard ({product:{display_name, qty, price}}, onRemove)  {

  const totalPrice = qty * price;

  return (
    <TouchableOpacity onPress={onRemove} style={styles.container}>
      <View>
        <Text style={styles.title}>{display_name}</Text>
        <Text style={styles.qty}>Cantidad: {qty}</Text>
        <Text style={styles.price}>Precio: {price}</Text>
        <Text style={styles.totalPrice}>Total: {totalPrice}</Text>
      </View>
      <Text style={styles.buttonText}>Borrar</Text>
    </TouchableOpacity>
  );
};

const styles = StyleSheet.create({
  container: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    backgroundColor: '#fff',
    borderRadius: 8,
    padding: 16,
    marginVertical: 8,
    marginHorizontal: 16,
    shadowColor: '#000',
    shadowOpacity: 0.1,
    shadowRadius: 4,
    shadowOffset: { width: 0, height: 2 },
    elevation: 2,
  },
  title: {
    fontSize: 18,
    fontWeight: 'bold',
  },
  qty: {
    fontSize: 16,
    marginTop: 8,
  },
  price: {
    fontSize: 16,
    marginTop: 8,
  },
  totalPrice: {
    fontSize: 16,
    marginTop: 8,
  },
  buttonText: {
    color: '#f44336',
    fontSize: 16,
    fontWeight: 'bold',
  },
});
